class ApplicationTest {

    @Test fun firstTest() {
        withTestApplication(Application::module) {
            with(handleRequest(HttpMethod.Get, "/person")) {
                assertEquals(HttpStatusCode.OK, response.status())
                assertEquals("Test", response.content)
